
public class ConcatOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String x = "Hello";
		String y = "World";
		System.out.println(x+y);
		
		int a = 100;
		int b = 200;
		System.out.println(a+b);
		
		System.out.println(a+b+x+y);
		System.out.println(x+y+a+b);
		
		
		

	}

}
